<template>
    <div class="keys">
        <div class="path">
            <span @click="openKeyForPath({
                    key:'',
                    name:''
                })"><Icon type="ios-home-outline" /></span>
            <span v-show="showPathInput == false">
                <span v-for="(item,key) in paths" :key="key" @click="openKeyForPath(item)"><span v-if="key > 0 || paths.length == 1">/</span> {{ item.name }} </span>
            </span>
            
            <!-- 路径文本框 -->
            <Input v-show="showPathInput == true" @on-enter="enterPath" v-model="currentPath" :placeholder="$t('key.currentPathTips')" style="width: 300px;margin-top:-9px;font-size:28px;" />
            <span @click="showPathInput = !showPathInput" style="font-size:15px;margin-left:5px;">
                <Button type="primary" v-show="showPathInput == false" size="small">Edit</Button>
                <Button type="primary" v-show="showPathInput == true" size="small">Hide</Button>
            </span>

            <Poptip style="float:right;margin-top:-3px;" placement="left-end"
                confirm
                :title="$t('key.delConfirm')"
                @on-ok="delKeys">
            <Button  type="error">{{$t('public.multiDelete')}}</Button>
            </Poptip>

            <Button type="success" @click="addKeyInfoModel = true;addKeyInfo = {kType:'KEY'}" style="float:right;margin-right:6px;">{{$t('public.add')}}</Button>
            <!-- 语言 -->
            <RadioGroup v-model="lang" @on-change="changeLang" type="button" style="float:right;margin-right:6px;">
                <Radio label="en">EN</Radio>
                <Radio label="zh">ZH</Radio>
            </RadioGroup>
            <!-- 展示方式切换 -->
            <RadioGroup v-model="listType" @on-change="changeListType" type="button" style="float:right;margin-right:6px;">
                <Radio label="grid"><Icon type="md-grid" /></Radio>
                <Radio label="list"><Icon type="ios-list-box-outline" /></Radio>
            </RadioGroup>

        </div>

        <div v-if="keysList.length == 0 && listType == 'grid'">{{$t('key.notSubKey')}}</div>

        <div class="lists">

            <!-- grid方式展示 -->
            <div class="key-list-main" v-if="listType == 'grid'">
                <div v-for="(item,key) in keysList" :key="key" class="key-list" @click="checkKey(item)">
                    <Checkbox class="checkbox" v-model="item.check"></Checkbox>
                    <Tooltip :content="item.key" placement="top-start">
                    <div @click.stop="openKey(item)">
                        <img v-if="item.is_dir==false" src="../assets/imgs/file.png" alt="file" class="key-icon">
                        <img v-if="item.is_dir==true" src="../assets/imgs/folder.png" alt="file" class="key-icon">
                    </div>
                    <div class="title">
                        {{item.path}}
                    </div>
                    </Tooltip>
                </div>
            </div>

            <!-- 表格形式展示 -->
            <div class="table-list-main" v-if="listType == 'list'">
                <Table border :columns="columnsKey" :data="keysList" @on-selection-change="selectionChangeKeys"></Table>
            </div>


            <div style="clear:both"></div>
            
        </div>

        <!-- 查看弹框 -->
        <Drawer
            :width="50"
            v-model="showKeyInfoModel"
            :title="$t('key.editKey')">
            <Form :model="showKeyInfo" :label-width="80">
                <FormItem label="Key" prop="key">
                    <Input v-model="showKeyInfo.key" disabled placeholder="key"></Input>
                </FormItem>
                <FormItem label="Value" prop="value" >
                  <!-- <codemirror ref="code" v-model="showKeyInfo.value" :options="cmOption" style="line-height:20px;"></codemirror> -->
                    <!-- <Input v-model="showKeyInfo.value" type="textarea" :autosize="{minRows: 8,maxRows: 80}" placeholder="配置值"></Input> -->
                  <codemirror v-model="showKeyInfo.value" style="line-height:20px;"></codemirror>
                </FormItem>
                <FormItem>
                    <Button @click="saveKey" type="primary">{{$t('public.save')}}</Button>
                    <Button @click="showKeyInfoModel = false" style="margin-left: 8px">{{$t('public.close')}}</Button>
                </FormItem>

            </Form>
        </Drawer>

        <!-- 添加弹框 -->
        <Drawer
            :width="50"
            v-model="addKeyInfoModel"
            :title="$t('key.addKey')">
            <Form :model="addKeyInfo" :label-width="80">
                <FormItem label="Key" prop="key">
                    <Input v-model="addKeyInfo.key" placeholder="key">
                        <span slot="prepend">{{currentPath}}/</span>
                    </Input>
                </FormItem>
                <FormItem label="KeyType" prop="kType">
                    <RadioGroup v-model="addKeyInfo.kType">
                        <Radio label="KEY"></Radio>
                        <Radio label="DIR"></Radio>
                    </RadioGroup>
                </FormItem>
                
                <FormItem label="Value" prop="value" v-show="addKeyInfo.kType == 'KEY'">
                  <!-- <codemirror v-model="addKeyInfo.value" :options="cmOption" style="line-height:20px;"></codemirror> -->
                    <!-- <Input v-model="addKeyInfo.value" type="textarea" :autosize="{minRows: 8,maxRows: 80}" placeholder="配置值"></Input> -->
                  <codemirror v-model="addKeyInfo.value" :options="{mode: 'javascript'}" style="line-height:20px;border: 1px solid #dcdee2"></codemirror>
                </FormItem>

                <FormItem>
                    <Button @click="addKey" type="primary">{{$t('public.save')}}</Button>
                    <Button @click="addKeyInfoModel = false" style="margin-left: 8px">{{$t('public.close')}}</Button>
                </FormItem>

            </Form>
        </Drawer>

    </div>
</template>

<script>
require("codemirror/mode/javascript/javascript");
require("codemirror/mode/toml/toml");
require("codemirror/mode/yaml/yaml");
require("codemirror/mode/xml/xml");

export default {
  data() {
    return {
      lang:'en',
      showPathInput: false, // 是否显示路径文本框
      listType: "grid", // 显示方式
      paths: [
        {
          key: "/",
          name: ""
        }
      ], // 路径
      currentPath: "", // 当前key路径
      showKeyInfoModel: false, // 配置详情是否显示
      showKeyInfo: {}, // 要显示的配置值

      addKeyInfoModel: false, // 添加弹框
      addKeyInfo: {}, // 添加时对象

      selectionKeys: [], // 表格选中列表

      keysList: [],

      columnsKey: [
        // 表格形式展示表头
        {
          type: "selection",
          width: 60,
          align: "center"
        },
        {
          title: "TYPE",
          width: 70,
          render: (h, params) => {
            return h("img", {
              attrs: {
                src:
                  params.row.is_dir == true
                    ? "data:image/png;base64,"
                    : "data:image/png;base64,",
                style: "width:35px;height:35px"
              },
              on: {
                click: () => {
                  this.openKey(params.row);
                }
              }
            });
          }
        },
        {
          title: "NAME",
          key: "path"
        },
        {
          title: "KEY",
          key: "key"
        },
        {
          title: " ",
          align: "center",
          render: (h, params) => {
            return h("div", [
              h(
                "Button",
                {
                  props: {
                    type: "primary",
                    size: "small"
                  },
                  style: {
                    marginRight: "5px"
                  },
                  on: {
                    click: () => {
                      this.openKey(params.row);
                    }
                  }
                },
                params.row.is_dir == true ? this.$t('key.open') : this.$t('key.show')
              ),
              h(
                "Poptip",
                {
                  props: {
                    confirm: true,
                    title: "确定删除"
                  },
                  on: {
                    "on-ok": () => {
                      this.delOneKey(params.row.key);
                    }
                  }
                },
                [
                  h(
                    "Button",
                    {
                      props: {
                        type: "error",
                        size: "small"
                      }
                    },
                    this.$t('public.delete')
                  )
                ]
              )
            ]);
          }
        }
      ],
      // 代码编辑器配置
      cmOption: {
        tabSize: 4,
        styleActiveLine: true,
        lineNumbers: true,
        line: true,
        mode: "text/javascript",
        lineWrapping: true,
        extraKeys: { Ctrl: "autocomplete" },
        theme: "monokai",
        autoRefresh: true
      }
    };
  },
  mounted() {
    this.lang = localStorage.getItem('lang') || 'en';
    this.listType = localStorage.getItem("list_type") || 'grid';
    this.getKeyList();
  },
  methods: {
    // 路径文本框回车
    enterPath() {
      this.openKeyForPath({
        key: this.currentPath,
        name: ""
      });
    },
    // 选中key
    checkKey(item) {
      item.check = !item.check;
    },
    // 打开key
    openKey(item) {
      console.log(item);
      if (item.is_dir == false) {
        this.showKeyInfo = item;
        this.showKeyInfoModel = true;
      } else {
        this.currentPath = item.key;
        let subarr = item.key.substring(1).split("/");
        this.paths.push({
          key: item.key,
          name: subarr[subarr.length - 1]
        });
        // console.log(this.paths);
        this.getKeyList();
      }
    },
    // 顶部路径打开目录
    openKeyForPath(item) {
      this.currentPath = item.key;
      let subKey = "";
      this.paths = [
        {
          key: "/",
          name: ""
        }
      ];
      if (item.key == "") {
        this.currentPath = "";
        console.log(this.paths);
        this.getKeyList();
        return;
      }

      item.key
        .substring(1)
        .split("/")
        .forEach(val => {
          subKey += "/" + val;
          this.paths.push({
            key: subKey,
            name: val
          });
        });
      console.log(this.paths);
      this.getKeyList();
    },

    // 添加key
    addKey() {
      if (this.addKeyInfo.key == "" || typeof this.addKeyInfo.key == "undefined") {
        this.$Message.warning("key不能为空");
        return;
      }
      this.addKeyInfo.key = this.currentPath + "/" + this.addKeyInfo.key;
      let uri = `/kv${this.addKeyInfo.key}`;
      console.log(this.addKeyInfo.kType);
      if (this.addKeyInfo.kType == "DIR") {
        uri += "?dir";
      }
      this.$http
        .post(uri, {
          value: this.addKeyInfo.value
        })
        .then(resp => {
          if (resp.data.err == "") {
            this.$Message.success("添加成功！");
            this.getKeyList();
            this.addKeyInfo = {};
            this.addKeyInfoModel = false;
          } else {
            this.$Message.error(resp.data.err);
          }
        });
    },

    saveKey() {
      this.$http
        .put(`/kv${this.showKeyInfo.key}`, {
          value: this.showKeyInfo.value
        })
        .then(resp => {
          if (resp.data.err == "") {
            this.$Message.success("保存成功！");
            this.showKeyInfoModel = false;
          } else {
            this.$Message.error(resp.data.err);
          }
        });
    },

    // 删除key
    delKeys() {
      this.keysList.forEach((val, index) => {
        // console.log(val._checked);
        if (val.check == true && this.listType == "grid") {
          this.delOneKey(val.key);
        }
      });
      if (this.listType == "list") {
        this.selectionKeys.forEach(val => {
          this.delOneKey(val.key);
        });
        this.selectionKeys = [];
      }
    },

    delOneKey(key) {
      this.$http.delete(`/kv${key}`).then(resp => {
        if (resp.data.err == "") {
          this.getKeyList();
        } else {
          this.$Message.error(resp.data.err);
        }
      });
    },

    // 表格选中行
    selectionChangeKeys(selections) {
      this.selectionKeys = selections;
      console.log(selections);
    },

    // 获取key列表
    getKeyList() {
      let k = this.currentPath == "" ? "/" : this.currentPath;
      this.$http.get(`/kv${k}?list`).then(resp => {
        if (resp.data.err == "") {
          this.keysList = resp.data.result;
          this.keysList.forEach((val,kkk) => {
            
            val.check = false;
            val._checked = false;
            let subarr = val.key.substring(1).split("/");
            val.path = subarr[subarr.length - 1];

            this.$set(this.keysList,kkk, val);
            console.log(val);
          });
        }else{
          this.$Message.error(resp.data.err);
        }
      });
    },

    // 展现方式
    changeListType(){
      localStorage.setItem("list_type", this.listType || 'grid');
    },

    // 切换语言
    changeLang(){
      this.$i18n.locale = this.lang || 'en';
      localStorage.setItem("lang", this.lang || 'en');
    }


  }
};
</script>

